using System;
using System.IO;
using Xunit;

namespace PromptPayQrCode.Test
{
    public class ThaiPromptPayQrCodeImageTest
    {
        [Fact]
        public void Generate_PromptPay_Qr_With_Local_Phone_Number_No_Amount_Save_Image_Test()
        {
            var path = AppDomain.CurrentDomain.BaseDirectory;
            var filename = "test_qr";
			var phoneNumber = "0801234567";
            new PromptPayQrCode(phoneNumber).GeneratePromptPayQrCode(path, filename);
            Assert.True(File.Exists(string.Concat(path, filename, ".jpg")));
        }

        [Fact]
		public void Generate_PromptPay_Qr_With_Local_Phone_Number_No_Amount_Write_Stream_Test()
		{
            var stream = new MemoryStream();
			var phoneNumber = "0801234567";
            var assertString = "";
            new PromptPayQrCode(phoneNumber).GeneratePromptPayQrCodeWithMemoryStream(stream);
            var base64Result = Convert.ToBase64String(stream.ToArray());
            Assert.Equal(base64Result, assertString);
		}
    }
}
